package com.intermediate;

public abstract class Employee {

	private static int autoGeneratedEmpID;
	protected int employeeId;
	protected String employeeName;
	protected double employeeBasicSalary;
	protected double employeeTotalSalary;
	

	
	{
		employeeId=++autoGeneratedEmpID;
	}
	public Employee(String name, double basicSalary) {
		// TODO Auto-generated constructor stub
		this.employeeName=name;
		this.employeeBasicSalary=basicSalary;
		this.employeeTotalSalary=this.employeeBasicSalary;
	}
	
	public String getEmployeeName() {
		return employeeName;
	}
	
	public double getEmployeeBasicSalary() {
		return employeeBasicSalary;
	}
	
	public int getEmployeeId() {
		return employeeId;
	}
	
	public double getEmployeeTotalSalary() {
		return employeeTotalSalary;
	}
	
	public void setEmployeeName(String employeeName) {
		this.employeeName = employeeName;
	}
	
	public abstract void calculateTotalSalary();
	
}
